mapper.select:
SELECT sum(b.reward) sumMoney,a.job_id jobId FROM c_entry_info a INNER JOIN b_job_info b ON a.job_id = b.id WHERE 1 = 1 AND a.over_datetime > TO_DAYS(NOW())
AND a.a_user_id = #{aUserId}
GROUP BY a.job_id

mapper.update:
UPDATE b_job_info b INNER JOIN ( SELECT sum(b.reward) money, b.id jobId FROM c_entry_info a INNER JOIN b_job_info b ON a.job_id = b.id LEFT JOIN c_resume_info c ON a.resume_id = c.id WHERE 1 = 1
AND a.a_user_Id = #{aUserId}
AND a.over_datetime < NOW() AND a.is_cash = 0 GROUP BY b.id ) x ON b.id = x.jobId SET b.total_amount = b.total_amount - x.money

update c_entry_info a SET a.is_cash = 1 WHERE a.id in (SELECT id FROM( SELECT a.id FROM c_entry_info a INNER JOIN b_job_info b ON a.job_id = b.id LEFT JOIN c_resume_info c ON a.resume_id = c.id WHERE 1 = 1
AND a.a_user_Id = #{aUserId}
AND a.over_datetime < NOW() AND a.is_cash = 0) x )
