mapper.select:
SELECT a.id, a.job_name jobName, a.num, a.description, a.age_min ageMin, a.age_max ageMax, a.education_degree educationDegree, a.work_experience workExpenience, a.probation, a.trial_time trialTime, a.trial_salary trialSalary, a.official_salary officialSalary, a.avg_salary avgSalary, a.reward, a.interview_time interviewTime, a.overtime, a. status, a.create_time createTime, a.label, b.create_time recriptTime, b.status collectionJobStatus,c.address FROM b_job_info a INNER JOIN a_collection_job_info b ON a.id = b.job_id LEFT JOIN b_store_info c ON a.b_store_id = c.id WHERE 1 = 1
AND b.a_user_id = #{aUserId}
AND b.status = #{status}

SELECT a.id, a.job_name jobName, a.num, a.description, a.age_min ageMin, a.age_max ageMax, a.education_degree educationDegree, a.work_experience workExpenience, a.probation, a.trial_time trialTime, a.trial_salary trialSalary, a.official_salary officialSalary, a.avg_salary avgSalary, a.reward, a.interview_time interviewTime, a.overtime, a. status, a.create_time createTime, a.label, ,b.address FROM b_job_info a INNER JOIN b_store_info b ON a.b_store_id = b.id WHERE 1 = 1
AND a.b_user_id = #{bUserId}
AND a.job_name = #{jobName}
AND a.status = #{status}
ORDER BY a.create_time ASC
