mapper.select:
SELECT a.id receviedId, a.latest_feedback latestFeedback , a.job_id jobId, a.create_time receviedTime, b.id resumeId, b.name, b.avatar, c.job_name jobName FROM b_recevied_info a INNER JOIN c_resume_info b ON a.resume_id = b.id LEFT JOIN b_job_info c ON a.job_id = c.id WHERE 1 = 1
AND a.a_user_id = #{aUserId}
AND a.job_id = #{jobId}
AND a.status = #{status}
AND b.name LIKE concat(concat("%",#{keyWord}),"%") OR b.phone LIKE concat(concat("%",#{keyWord}),"%")
ORDER BY a.create_time DESC

SELECT * FROM C_RESUME_INFO a WHERE 1 = 1
AND a.a_user_id = #{aUserId}
AND a.status = #{status}
AND a.`name` LIKE concat(concat("%",#{keyWord}),"%") OR a.phone LIKE concat(concat("%",#{keyWord}),"%")
ORDER BY create_time DESC

SELECT a.job_name jobName,b.create_time time,b.latest_feedback statusContent,c.`name` storeName FROM b_job_info a INNER JOIN b_recevied_info b ON a.id = b.job_id LEFT JOIN b_store_info c ON a.b_store_id = c.id WHERE 1 = 1
AND b.resume_id = #{resumeId}
ORDER BY time ASC

SELECT a.id receviedId, a.latest_feedback latestFeedback, a.create_time receviedTime, b.id resumeId, b.`status`, c.job_name jobName, c.id jobId, b.avatar, b.name FROM b_recevied_info a INNER JOIN c_resume_info b ON a.resume_id = b.id LEFT JOIN b_job_info c ON a.job_id = c.id WHERE 1 = 1
AND a.b_user_id = #{bUserId}
AND a.status = #{status}
AND b.name LIKE concat(concat("%",#{keyWord}),"%") OR b.phone LIKE concat(concat("%",#{keyWord}),"%")
ORDER BY a.create_time ASC
